package com.catnip.todolistapp.data.datasource

import com.catnip.todolistapp.data.model.Task

class TaskDataSource {

    private var tasks = mutableListOf<Task>().apply {
        add(
            Task(
                1,
                "Mencuci Baju",
                "Banyak baju yang harus dicuci",
                "data:image/jpeg;base64,",
                true
            )
        )
        add(
            Task(
                2,
                "Mencuci Piring",
                "Banyak piring yang harus dicuci",
                "data:image/jpeg;base64,",
                true
            )
        )
        add(
            Task(
                3,
                "Mencuci Gelas",
                "Banyak gelas yang harus dicuci",
                "data:image/jpeg;base64,",
                true
            )
        )
        add(
            Task(
                4,
                "Mencuci Baju",
                "Banyak baju yang harus dicuci 2",
                "data:image/jpeg;base64,",
                false
            )
        )
        add(
            Task(
                5,
                "Mencuci Piring",
                "Banyak piring yang harus dicuci 2",
                "data:image/jpeg;base64,",
                false
            )
        )
        add(
            Task(
                6,
                "Mencuci Gelas",
                "Banyak gelas yang harus dicuci 2",
                "data:image/jpeg;base64,",
                false
            )
        )
    }

    fun add(task: Task) {
        tasks.add(task)
    }

    fun delete(taskId: Int) {
        tasks.remove(tasks.first {
            it.id == taskId
        })
    }

    fun changeTaskStatus(taskId: Int) {
        tasks.first {
            it.id == taskId
        }.apply {
            isTaskComplete = isTaskComplete.not()
        }
    }

    fun getData(isTaskCompleted: Boolean): List<Task> {
        return tasks.filter { it.isTaskComplete == isTaskCompleted }
    }
}